mapper.select:
	select id, orderId, tranceId, money, channel, state, remark, notify_url, careate_time, update_time, payorderId from t_pay_detail t
		where t.state in (7,8,4,6)
  	and t.tranceId = #{tranceId,jdbcType=VARCHAR}
  	and t.channel = #{channel,jdbcType=VARCHAR}
  	and t.orderId = #{orderId,jdbcType=VARCHAR}
  	order by t.id desc
  	limit 3

mapper.update:
update t_pay_detail set state=#{state},update_time = #{updateTime,jdbcType=TIMESTAMP}
where orderId = #{orderid,jdbcType=VARCHAR}
and tranceId is not NULL
and channel = #{channel,jdbcType=VARCHAR}

mapper.delete:
    delete from t_pay_detail where 1=1
    
      and orderId = #{params.orderId,jdbcType=VARCHAR}
    
    
      and channel = #{params.channel,jdbcType=VARCHAR}
